/**
 * @author Rido Martupa Simbolon
 */

import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';
import axios from "axios";
import '../../css/project.css';

const Users = () => {
  const [users, setUsers] = useState([]);

  const getUsers = async () => {
    const response = await axios.get("http://192.168.10.30:9000/users");
    setUsers(response.data);
  };
  
  useEffect(() => {
    getUsers();
  }, [getUsers]);

  return (
    <div className="container-fluid mt-4">
      {/* Page Heading */}
      <div className="d-sm-flex align-items-center justify-content-between mb-2">
        <h1 className="h3 mb-3 text-gray-800">All Users</h1>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><NavLink href="/dashboard">Dashboard</NavLink></li>
            <li className="breadcrumb-item active" aria-current="page">Users</li>
          </ol>
        </nav>
      </div>
      <div className="card card-body">
        <div className="my-1">
          <Link to={'/users/new'} className="d-sm-inline-block btn btn-sm btn-primary shadow-sm mb-3"><i className="fas fa-plus-square fa-sm text-white-50" /> Create New User</Link>
        </div>
        <table className="table table-bordered">
          <thead>
            <tr className="text-center">
              <th scope="col">ID</th>
              <th scope="col">Name</th>
              <th scope="col">Username</th>
              <th scope="col">Role</th>
              <th scope="col">CreatedAt</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>

          {users.map((user, index) => (
            <tr key={index}>
              <td className="text-center">{user.id}</td>
              <td className="text-bold">
                <Link className="text-lead toltip" to={`/users/detail/${user.uuid}`}>{user.name}
                  <small className="tooltiptext">click for detail task</small>
                </Link>
              </td>
              <td className="text-center">{user.email}</td>
              <td className="text-center">{user.role}</td>
              <td className="text-center">{user.createdAt}</td>
              <td className="text-center">
                <button className="btn btn-sm btn-info" title="Reset Password User"><i className="fa fa-edit-square"></i> Reset</button>
              </td>
            </tr>
          ))}

          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Users;
