import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";
import Moment from 'moment';
import Loader from './Loader';
import DataTable from 'react-data-table-component';


const AllNotif = () => {
  const [pending, setPending] = useState(true);
	const [rows, setRows] = useState([]);
	const [data, setData] = useState([]);
  const navigate = useNavigate();

  const columns = [
      {
        name: 'ID',
        selector: row => row.id
      },
      {
        name: 'FROM',
        selector: row => row.name
      },
      {
        name: 'DESCRIPTION',
        selector: row => row.description
      },
      {
        name: 'TYPE',
        selector: row => row.trxtype
      },
      {
        name: 'STATUS',
        selector: row => row.read_status
      },
      {
        name: 'DATE',
        selector: row => row.createdAt
      }
    ];

  const getNotif = async () => {
    setData([]);
    const response = await axios.get("http://192.168.10.30:9000/notif");
      response.data.forEach((items,idx) => {
        let dataRow = { 
          key: idx,
          id: <Link onClick={() => handleNotifClick(items.foreignid)} to=''>{items.id}</Link>, 
          name: items.name, 
          description: items.description, 
          trxtype: items.trxtype.toUpperCase(), 
          read_status: items.read_status ? 'Read' : 'Unread', 
          createdAt: Moment(items.createadAt).format('DD-MM-YYYY HH:mm') 
        };
        setData(oldArray => [...oldArray,dataRow]);
      });
  };  

	useEffect(() => {
		const timeout = setTimeout(() => {
			getNotif();
			setPending(false);
		}, 2000);
		return () => clearTimeout(timeout);
	}, []);

  const handleNotifClick = async(e) => {
    try {
      const response = await axios.post(`http://192.168.10.30:9000/notif/update/${e}`);
      if (response.data.status === 201) {
        navigate(`../projects/detail/${e}`);
      } else {
        toast.error(<small>Gagal update data notif!</small>, {
          theme: "colored"
        });
        setTimeout(() => {
          window.location.reload();
        }, 3000);
      }
    } catch (error) {
      toast.error(<small>Gagal update data notif! msg: {error}</small>, {
        theme: "colored"
      });
      setTimeout(() => {
        window.location.reload();
      }, 3000);
    }
  }

  return (
    <div className="container-fluid mt-4">
      {/* Page Heading */}
      <div className="d-sm-flex align-items-center justify-content-between mb-2">
        <h1 className="h3 mb-3 text-gray-800">All Notification Project</h1>
        <Link to={'/projects/new'} className="d-sm-inline-block btn btn-sm btn-primary shadow-sm mb-3"><i className="fas fa-plus-square fa-sm text-white-50" /> Create New Project</Link>
      </div>
      
      <div className="card shadow mb-4">
        <div className="card-body">
          <div className="table-responsive">

          <DataTable
            title="Notification List"
            columns={columns}
            data={data}
            progressPending={pending}
            progressComponent={<Loader />}
            pagination
          />
            {/* <table className="table table-bordered" id="dataNotif" width="100%" cellSpacing={0}>
              <thead>
                <tr>
                  <th className="text-center">ID</th>
                  <th className="text-center">Description</th>
                  <th className="text-center">Type</th>
                  <th className="text-center">Status</th>
                  <th className="text-center">Date</th>
                </tr>
              </thead>
              <tbody>

              {notif.map((item, index) => (
                <tr key={index}>
                  <td className="text-center">
                    <Link className="" to="">
                      {item.id}
                    </Link>
                  </td>
                  <td className="text-center">{item.description}</td>
                  <td className="text-center">{item.trxtype}</td>
                  <td className="text-center">{item.read_status ? 'READ' : 'UNREAD'}</td>
                  <td className="text-center">{Moment(item.createadAt).format('DD-MM-YYYY HH:mm')}</td>
                </tr>
              ))}
                
              </tbody>
            </table> */}
          </div>
        </div>
      </div>

    </div>
  );
}

export default AllNotif;
