import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Moment from 'moment';
import Loader from './Loader';
import DataTable from 'react-data-table-component';
import { ToastContainer, toast } from 'react-toastify';

const Task = () => {
  const [pending, setPending] = useState(true);
  const [tasks, setTasks] = useState('');
  const { user } = useSelector((state) => state.auth);
  const navigate = useNavigate();

  let uuid = '';
  if(user !== null){
    uuid = user.uuid;
  }

  const columns = [
    {
      name: 'PROJECT',
      selector: row => row.projectname,
      sortable: true
    },
    {
      name: 'TASKID',
      selector: row => row.taskid,
      sortable: true
    },
    {
      name: 'FROM',
      selector: row => row.name,
      sortable: true
    },
    {
      name: 'TASK',
      selector: row => row.taskname,
      sortable: true,
      wrap: true
    },
    {
      name: 'TASK',
      selector: row => row.taskname,
      sortable: true,
      wrap: true
    },
    {
      name: 'SUB TASK',
      selector: row => row.trxtype,
      sortable: true
    },
    {
      name: 'STATUS',
      selector: row => row.read_status,
      sortable: true
    },
    {
      name: 'DATE',
      selector: row => row.createdAt,
      sortable: true
    }
  ];
  
  const getAllTask = async () => {
    try {
      let response = await axios.get(`http://192.168.10.30/tasksbyuser/${uuid}`)
    } catch (error) {
      toast.error(<small>Gagal reload data task! msg: {error}</small>, {
        theme: "colored"
      });
      setTimeout(() => {
        window.location.reload();
      }, 2000);
    }
  }

  useEffect(() => {
    getAllTask();
  }, []);

  return (
    <div className="container-fluid mt-4">
      {/* Page Heading */}
      <div className="d-sm-flex align-items-center justify-content-between mb-2">
        <h1 className="h3 mb-3 text-gray-800">All My Tasks</h1>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><a href="/dashboard">Dashboard</a></li>
            <li className="breadcrumb-item active" aria-current="page">Task</li>
          </ol>
        </nav>
      </div>
      
      <div className="card shadow mb-4">
        <div className="card-body">
          <div className="table-responsive">

          <DataTable
            title="Task List"
            columns={columns}
            data={data}
            progressPending={pending}
            progressComponent={<Loader />}
            pagination
          />
        
          </div>
        </div>
      </div>

      <ToastContainer icon={false} autoClose={3000}/>

    </div>
  );
}

export default Task;
