import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";
import Moment from 'moment';
import Table from './Table';


const AllNotif = () => {
  const [notif, setNotif] = useState([]);

  const getAllNotif = async () => {
    const response = await axios.get("http://192.168.10.30:9000/notif");
    setNotif(response.data);
  };
  
  useEffect(() => {
    getAllNotif();
  }, [getAllNotif]);

  return (
    <div className="container-fluid mt-4">
      {/* Page Heading */}
      <div className="d-sm-flex align-items-center justify-content-between mb-2">
        <h1 className="h3 mb-3 text-gray-800">All Notification Project</h1>
        <Link to={'/projects/new'} className="d-sm-inline-block btn btn-sm btn-primary shadow-sm mb-3"><i className="fas fa-plus-square fa-sm text-white-50" /> Create New Project</Link>
      </div>
      
      <div className="card shadow mb-4">
        <div className="card-header py-3">
          <h6 className="m-0 font-weight-bold text-primary">Data Notification</h6>
        </div>
        <div className="card-body">
          <div className="table-responsive">

            <Table colums={columsTable} data={notif}></Table>
